template.value:
import java.util.ArrayList;
import java.util.List;

class Student {
    private java.lang.String name;
    private int rollNo;

    Student(java.lang.String name, int rollNo){
        this.name = name;
        this.rollNo = rollNo;
    }

    public java.lang.String getName() {
        return name;
    }

    public void setName(java.lang.String name) {
        this.name = name;
    }

    public int getRollNo() {
        return rollNo;
    }

    public void setRollNo(int rollNo) {
        this.rollNo = rollNo;
    }
}

interface StudentDao {
    public java.util.List<Student> getAllStudents();
    public Student getStudent(int rollNo);
    public void updateStudent(Student student);
    public void deleteStudent(Student student);
}

class StudentDaoImpl implements StudentDao {

    //list is working as a database
    java.util.List<Student> students;

    public StudentDaoImpl(){
        students = new java.util.ArrayList<Student>();
        Student student1 = new Student("Robert",0);
        Student student2 = new Student("John",1);
        students.add(student1);
        students.add(student2);
    }
    @java.lang.Override
    public void deleteStudent(Student student) {
        students.remove(student.getRollNo());
        java.lang.System.out.println("Student: Roll No " + student.getRollNo() + ", deleted from database");
    }

    //retrive list of students from the database
    @java.lang.Override
    public java.util.List<Student> getAllStudents() {
        return students;
    }

    @java.lang.Override
    public Student getStudent(int rollNo) {
        return students.get(rollNo);
    }

    @java.lang.Override
    public void updateStudent(Student student) {
        students.get(student.getRollNo()).setName(student.getName());
        java.lang.System.out.println("Student: Roll No " + student.getRollNo() + ", updated in the database");
    }
}

class DaoPatternDemo {
    public static void main(java.lang.String[] args) {
        StudentDao studentDao = new StudentDaoImpl();

        //print all students
        for (Student student : studentDao.getAllStudents()) {
            java.lang.System.out.println("Student: [RollNo : " + student.getRollNo() + ", Name : " + student.getName() + " ]");
        }


        //update student
        Student student =studentDao.getAllStudents().get(0);
        student.setName("Michael");
        studentDao.updateStudent(student);

        //get the student
        studentDao.getStudent(0);
        java.lang.System.out.println("Student: [RollNo : " + student.getRollNo() + ", Name : " + student.getName() + " ]");
    }
}
